export declare const GET_ITEM_CHILDREN = "\nquery GET_ITEM_CHILDREN (\n  $language: String!\n  $id: ID!\n) {\n  tree {\n    getNode (\n      language: $language\n      itemId: $id\n    ) {\n      children {\n        id: itemId\n        treePosition: position\n        path\n        item { name }\n      }\n    }\n  }\n}\n";
export declare const GET_ITEM_QUERY = "\nquery GET_ITEM($language: String!, $id: ID!, $version: VersionLabel!) {\n  item {\n    get(id: $id, language: $language, versionLabel: $version) {\n      ...item\n      ...product\n    }\n  }\n}\n\nfragment item on Item {\n  id\n  name\n  type\n  tree {\n    path\n    position\n  }\n  externalReference\n  shape {\n    identifier\n  }\n  topics {\n    path\n  }\n  components {\n    ...rootComponent\n  }\n}\n\nfragment primitiveComponentContent on ComponentContent {\n  ...singleLineContent\n  ...richTextContent\n  ...imageContent\n  ...videoContent\n  ...fileContent\n  ...paragraphCollectionContent\n  ...itemRelationsContent\n  ...gridRelationsContent\n  ...locationContent\n  ...selectionContent\n  ...booleanContent\n  ...propertiesTableContent\n  ...dateTimeContent\n  ...numericContent\n}\n\nfragment product on Product {\n  vatType {\n    name\n    percent\n  }\n  variants {\n    externalReference\n    name\n    sku\n    isDefault\n    attributes {\n      attribute\n      value\n    }\n    priceVariants {\n      identifier\n      price\n    }\n    stock\n    stockLocations {\n      identifier\n      name\n      stock\n    }\n    images {\n      ...image\n    }\n    subscriptionPlans {\n      identifier\n      name\n      periods {\n        id\n        name\n        initial {\n          ...subscriptionPlanPricing\n        }\n        recurring {\n          ...subscriptionPlanPricing\n        }\n      }\n    }\n\n    components {\n      ...rootComponent\n    }\n  }\n}\n\nfragment rootComponent on Component {\n  id: componentId\n  name\n  type\n  content {\n    ...primitiveComponentContent\n    ... on ComponentChoiceContent {\n      selectedComponent {\n        id: componentId\n        name\n        type\n        content {\n          ...primitiveComponentContent\n        }\n      }\n    }\n    ... on ContentChunkContent {\n      chunks {\n        id: componentId\n        name\n        type\n        content {\n          ...primitiveComponentContent\n        }\n      }\n    }\n  }\n}\n\nfragment image on Image {\n  url\n  altText\n  caption {\n    json\n  }\n}\n\nfragment video on Video {\n  id\n  title\n  playlist(type: \"m3u8\")\n  thumbnails {\n    ...image\n  }\n}\n\nfragment file on File {\n  url\n  title\n}\n\nfragment dateTimeContent on DatetimeContent {\n  datetime\n}\n\nfragment numericContent on NumericContent {\n  number\n  unit\n}\n\nfragment propertiesTableContent on PropertiesTableContent {\n  sections {\n    title\n    properties {\n      key\n      value\n    }\n  }\n}\n\nfragment booleanContent on BooleanContent {\n  value\n}\n\nfragment selectionContent on SelectionContent {\n  options {\n    key\n    value\n  }\n}\n\nfragment imageContent on ImageContent {\n  images {\n    ...image\n  }\n}\n\nfragment videoContent on VideoContent {\n  videos {\n    ...video\n  }\n}\n\nfragment fileContent on FileContent {\n  files {\n    ...file\n  }\n}\n\nfragment singleLineContent on SingleLineContent {\n  text\n}\n\nfragment richTextContent on RichTextContent {\n  json\n}\n\nfragment itemRelationsContent on ItemRelationsContent {\n  items {\n    id\n    tree {\n      path\n    }\n    externalReference\n  }\n  productVariants {\n    sku\n  }\n}\n\nfragment gridRelationsContent on GridRelationsContent {\n  grids {\n    name\n  }\n}\n\nfragment locationContent on LocationContent {\n  lat\n  long\n}\n\nfragment paragraphCollectionContent on ParagraphCollectionContent {\n  paragraphs {\n    title {\n      ...singleLineContent\n    }\n    body {\n      ...richTextContent\n    }\n    images {\n      ...image\n    }\n  }\n}\n\nfragment subscriptionPlanPricing on ProductVariantSubscriptionPlanPricing {\n  period\n  unit\n  priceVariants {\n    identifier\n    price\n  }\n  meteredVariables {\n    id\n    identifier\n    name\n    tierType\n    tiers {\n      threshold\n      priceVariants {\n        identifier\n        price\n      }\n    }\n  }\n}\n";
